    # Translates Add_Student attributes
    def retranslate_ui(self, Dialog):
        _translate = QtCore.QCoreApplication.translate
        Dialog.setWindowTitle(_translate("Dialog", "Dialog"))
        self.first_label.setText(_translate("Dialog", "First Name:"))
        self.last_label.setText(_translate("Dialog", "Last Name:"))
        self.year_label.setText(_translate("Dialog", "Admittance Year:"))
        self.submit.setText(_translate("Dialog", "Submit"))
        self.cancel.setText(_translate("Dialog", "Cancel"))
        self.label.setText(_translate("Dialog", "    Add Student"))

    # Displays a message box confirming a student was added to the database
    def message_box(self, first_name, last_name):
        msg = QMessageBox()
        img = self.load_image("https://www.clipartkey.com/mpngs/m/64-645574_online-student-icon.png").scaled(150, 150, QtCore.Qt.KeepAspectRatio)
        msg.setIconPixmap(img)
        msg.setWindowTitle("Record Added Successfully")
        msg.setText("Thank You.  A record for " + first_name + " " + last_name + " has been added to the system.")
        x = msg.exec_()

    # Loads an image from a link to a pixmap
    def load_image(self, link):
        img = QImage()
        img.loadFromData(self.read_img(link))

        pixmap = QPixmap.fromImage(img)
        return pixmap

    # Reads an image from a link as bytes
    def read_img(self, link):
        img_bytes = urllib.request.urlopen(link).read()
        return img_bytes

    # Retrieves user input and adds new student into the database
    def add_student(self):
        first_name = self.first_name_entry.text()
        last_name = self.last_name_entry.text()
        admittance_year = self.admit_year_entry.text()
        new_student = StudentAccount(first_name, last_name, admittance_year)
        record = new_student.get_record()
        db_manager = DBManager()
        db_manager.add_record(record)
        self.message_box(first_name, last_name)
        self.first_name_entry.setText("")
        self.last_name_entry.setText("")
        self.admit_year_entry.setText("")

    # Hides the Add_Student dialog
    def cancel_app(self):
        self.hide()


